  invalid_index &lt;- lapply(str_split(serial_sequence, ""), \(x) {

    tmp &lt;- str_split(x, "") |&gt; unlist()

    check_pairs &lt;- 2

    repeat {


      if(length(unique(tmp)) == 1) {

        return(paste(tmp, collapse = ""))

      }

      tmp &lt;- str_split(x, "") |&gt; unlist()

      if(check_pairs &gt; length(tmp)) return(NULL)

      element_length &lt;- length(tmp) / check_pairs

      if(round(element_length) != element_length) {

        check_pairs &lt;- check_pairs + 1

        next
      }

      start &lt;- seq(1, length(tmp), element_length)
      stop &lt;- seq(element_length, length(tmp), element_length)

      tmp &lt;- lapply(1:length(start), \(k) {

        str_c(tmp[start[k]:stop[k]], collapse = "")

      }) |&gt; unlist()

      check_pairs &lt;- check_pairs + 1

    }

  }) |&gt; unlist()

  return(invalid_index)

}, mc.cores = detectCores()-4) 


